parameters.functionCode:
// Validate incoming purchase request
const data = $input.item.json;

// Required fields
const required = ['purchase_request_id', 'requester', 'items', 'total_amount', 'supplier'];
const missing = required.filter(field => !data[field]);

if (missing.length > 0) {
  throw new Error(`Missing required fields: ${missing.join(', ')}`);
}

// Validate requester
if (!data.requester.name || !data.requester.email) {
  throw new Error('Requester name and email are required');
}

// Validate items
if (!Array.isArray(data.items) || data.items.length === 0) {
  throw new Error('At least one item is required');
}

// Validate total_amount
if (typeof data.total_amount !== 'number' || data.total_amount <= 0) {
  throw new Error('Total amount must be a positive number');
}

return {
  json: {
    ...data,
    validated: true,
    validated_at: new Date().toISOString()
  }
};

