let approvalLevel, approverRole, slaHours;

if (amount < 1000) {
  approvalLevel = 'auto';
  approverRole = 'Auto-aprobaciÃ³n';
  slaHours = 0;
} else if (amount < 5000) {
  approvalLevel = 'manager';
  approverRole = 'Gerente Compras';
  slaHours = 24;
} else if (amount < 20000) {
  approvalLevel = 'director';
  approverRole = 'Director Operaciones';
  slaHours = 48;
} else {
  approvalLevel = 'ceo';
  approverRole = 'CEO';
  slaHours = 72;
}

return {
  json: {
    ...$input.item.json,
    approval_level: approvalLevel,
    approver_role: approverRole,
    sla_hours: slaHours,
    is_auto_approval: approvalLevel === 'auto'
  }
};

// Calculate SLA deadline
const slaHours = $input.item.json.sla_hours;
const deadline = new Date();
deadline.setHours(deadline.getHours() + slaHours);

return {
  json: {
    ...$input.item.json,
    sla_deadline: deadline.toISOString()
  }
};

